<?phprequire('../config.php');require('../lib/Db.class.php');require('../lib/Input.class.php');$password1=Input::itrim($_POST['password']);$password2=Input::itrim($_POST['password_confirm']);if($password1!=$password2){	echo "{success:false,msg:'两次输入的密码不一致'}";	exit;}$db=new Db();$db->query("UPDATE tg_users SET            password='".sha1($password1)."'			WHERE username='root'");			echo "{success:true,msg:'更新成功'}";?>