<?php/********************************* 添加企业的脚本*********************************/require('../../lib/Db.class.php');require('../../lib/Input.class.php');//获取传值$id         =Input::itrim($_POST['id']);$stuff_index=Input::itrim($_POST['stuff_index']);$stuff_name =Input::itrim($_POST['stuff_name']);$status     =Input::itrim($_POST['status']);$notion     =Input::itrim($_POST['notion']);//实例化数据库类$db=new Db();if($id){	//编辑操作	/**************************	*	* 检查企业编号是否重复	*	**************************/	$num=$db->getNum("SELECT id 	                  FROM tg_stuffs 					  WHERE stuff_index='$stuff_index' 					  AND id !='$id'");					  	if($num>0)	{		echo "{success:false,msg:'该企业编号已存在'}";		exit;	}		//更新	$db->query("UPDATE tg_stuffs SET 				stuff_index='$stuff_index',				stuff_name ='$stuff_name',				status     ='$status',				notion     ='$notion'				WHERE id='$id'");			echo "{success:true,msg:'更新成功'}";}else{	//添加操作	/**************************	*	* 检查是否重复	*	**************************/	$num=$db->getNum("SELECT id 				      FROM tg_stuffs 					  WHERE stuff_index='$stuff_index'");	if($num>0)	{		echo "{success:false,msg:'该企业编号已存在'}";		exit;	}		//添加	$db->query("INSERT INTO tg_stuffs 	            (stuff_index,				 stuff_name,				 status,				 notion)				VALUES				('$stuff_index',				 '$stuff_name',				 '$status',				 '$notion')");				 				 	$stuff_id=mysql_insert_id();			//插入测试分类	$db->query("INSERT INTO tg_cates	           (cate_name,stuff_id)			   VALUES			   ('测试分类','$stuff_id')");			   	$cate_id=mysql_insert_id();			//插入测试关键词	$db->query("INSERT INTO tg_keywords                (keyword,cate_id,mark,stuff_id)				VALUES				('测试关键词','$cate_id','0','$stuff_id')");					//插入测试站点	$db->query("INSERT INTO tg_sites	           (site_name,site_url,stuff_id)			   VALUES			   ('测试站点','test.com','$stuff_id')");				 	echo "{success:true,msg:'添加成功'}";		}?>