<?php/********************************* 添加企业用户*********************************/require('../../config.php');require('../../lib/Stuff.class.php');require('../../lib/Input.class.php');$id      =Input::itrim($_POST['id']);$username=Input::itrim($_POST['username']);$password=Input::itrim($_POST['password']);$status  =Input::itrim($_POST['status']);$stuff_id=Input::itrim($_POST['stuff_id']);$stuff=new Stuff($stuff_id);if($stuff->checkUserRepeat($username,$id)){	echo "{success:false,msg:'该用户名已存在'}";	exit;}if($id){	//编辑操作	$stuff->editUser($id,$username,$password,$status);	echo "{success:true,msg:'更新成功'}";}else{	//添加操作	$stuff->addUser($username,$password,$status);	echo "{success:true,msg:'添加成功'}";}?>