<?php/********************************* 删除企业*********************************/require('../../lib/Db.class.php');require('../../lib/Input.class.php');//获取传值$ids=Input::itrim($_POST['ids']);//实例化数据库类$db=new Db();//删除企业$db->query("DELETE FROM tg_stuffs WHERE id IN ($ids)");//删除用户$db->query("DELETE FROM tg_users WHERE stuff_id IN ($ids)");//删除站点$db->query("DELETE FROM tg_sites WHERE stuff_id IN ($ids)");//删除分类$db->query("DELETE FROM tg_cates WHERE stuff_id IN ($ids)");//删除关键词$db->query("DELETE FROM tg_keywords WHERE stuff_id IN ($ids)");if(mysql_affected_rows()>0){	echo "删除成功";}else{	echo "删除失败";}?>