<?php/********************************* 企业列表*********************************/require('../../lib/Db.class.php');require('../../lib/Input.class.php');//获取传值$start=Input::itrim($_GET['start']);$limit=Input::itrim($_GET['limit']);//实例化数据库类$db=new Db();//读取数据$data['total']=$db->getNum("SELECT id FROM tg_stuffs ");$data['root']=$db->getSome("SELECT * FROM tg_stuffs                             ORDER BY id DESC 							LIMIT $start,$limit");echo json_encode($data);?>